def howmuch #1
puts "Nombre de handle total est de: #{$journalists.size}"
end


def thenumbers #2
number = 0	#compteur
n = 0		#anti doublons

$journalists.each do |journalist|
(0..9).each do |i|
	i = i.to_s
if journalist.match?(i) && n == 0
	number += 1
	n = 1
end

end
n = 0 #on revient a la pos. initiale
end
puts "Nombre de handle avec des numeros: #{number}"
end



def find_aude #3
number = 0
$journalists.each do |journalist|
	lower_case = journalist.downcase
if lower_case.match?("aude")
	number += 1
end #if
end #do

puts "Nombre de handle content la suite de characteres 'aude' est de: #{number}"
number = 0				
end #def


def lesmajusculesmaintenenttamere #4
number = 0
$journalists.each do |journalist|

if journalist.index(/@[A-Z]/)
	number += 1	
end
end
puts "Qui commencent par une majuscule t'en as: #{number}"
end


def onemaj #5
number = 0
$journalists.each do |journalist|

if journalist.index(/[A-Z]/)
	number += 1
end
end
puts "Avec aumoins une majuscule t'en as: #{number}"
end





def underscore #6
number = 0
$journalists.each do |journalist|
dispatch = journalist.split ""
dispatch.each do |i|
if i.match?(/[_]/)
	number += 1
end
end
end
puts "Avec les '_' t'en as: #{number}"
end




def alphabetizator #7
	puts $journalists.sort(&:casecmp)
end



def shorts #8

	$tabshorts = $journalists.sort_by(&:length)

	puts "Les 50 handle les plus courts sont: #{$tabshorts[0..49]}"#faut les 50
end



def laposition #9
	puts $journalists.index('@epenser')
end




def menu

    puts "1.Combien y a-t-il de journalistes dans ce array ?"
    puts "2.Combien d'handle contiennent un numéro ?"
